    function SetTopUpControlAvailability(ctrlID) {
        var additionalHoldingsChecked = false;
        $('input[id*="' + ctrlID + '"][type=checkbox]').each(
                        function() {
                            if ($(this).is(':checked')) {
                                additionalHoldingsChecked = true;
                            }
                        }
                        );

        if (additionalHoldingsChecked) {
            $('[type=checkbox][id$=chkTopup]').not('input[id*="' + ctrlID + '"]').attr('disabled', true);
            $('input[id$="btnTopup"]').not('[id*="' + ctrlID + '"]').attr('disabled', true);
            $('input[id$="btnTopup"]').not('[id*="' + ctrlID + '"]').addClass("disabled");
        }
        else {
            $('[type=checkbox][id$=chkTopup]').attr('disabled', false);
            $('input[id$="btnTopup"]').attr('disabled', false);
            $('input[id$="btnTopup"]').removeClass("disabled");
        }
    }

