					function showhide(id, ctlId, showImage, hideImage, showText, hideText)
					{
						if (document.getElementById)
						{
							var obj = document.getElementById(id);
							if (obj.style.display == 'none')
							{
								obj.style.display = '';
								var obj2 = document.getElementById(ctlId + '_hidden');
								obj2.value = 'show';
								var btn = document.getElementById(ctlId + '_img');
                                if (btn)
								    btn.src = hideImage;
								var txt = document.getElementById(ctlId + '_textdiv');
								txt.innerHTML  = hideText;
							}
							else
							{
								obj.style.display = 'none';
								var obj2 = document.getElementById(ctlId + '_hidden');
								obj2.value = 'hidden';
								var btn = document.getElementById(ctlId + '_img');
                                if (btn)
    								btn.src = showImage;
								var txt = document.getElementById(ctlId + '_textdiv');
								txt.innerHTML  = showText;
							}
						}
					}

