    function GetHiddenDateVal(ctlDateID) {
        var hiddenDOB = document.getElementById(ctlDateID + '_txtDateHidden');

        var txtDD = document.getElementById(ctlDateID + '_txtDD').value;
        var txtMM = document.getElementById(ctlDateID + '_txtMM').value;
        var txtYYYY = document.getElementById(ctlDateID + '_txtYYYY').value;

        if (txtDD != null && txtDD.length == 1) {
            txtDD = '0' + txtDD;
        }

        if (txtMM != null && txtMM.length == 1) {
            txtMM = '0' + txtMM;
        }

        var dateval = txtYYYY + txtMM + txtDD;
        hiddenDOB.value = dateval;
        return dateval;
    }

